import { Component } from '@angular/core';

@Component({
  selector: 'mr-upload-file',
  templateUrl: './mr-upload-file.component.html',
  styleUrls: ['./mr-upload-file.component.css']
})
export class MrUploadFileComponent {
  isDropZoneActive = false;

  imageSource = '';

  textVisible = true;

  progressVisible = false;

  progressValue = 0;

  solveButtonEnabled = false;

  isLoading = false;

  constructor() {
    this.onDropZoneEnter = this.onDropZoneEnter.bind(this);
    this.onDropZoneLeave = this.onDropZoneLeave.bind(this);
    this.onUploaded = this.onUploaded.bind(this);
    this.onProgress = this.onProgress.bind(this);
    this.onUploadStarted = this.onUploadStarted.bind(this);
  }

  private readonly DUMMY_RESPONSE_IMAGE = "data:image/png;base64,";

  async onMazeSolve() {
    const image = document.getElementById('dropzone-image') as HTMLImageElement || null;
    if (!image) {
      return;
    }

    this.solveButtonEnabled = false;
    this.isLoading = true;

    await delay(3500);
    if (image) {
      image.src = this.DUMMY_RESPONSE_IMAGE;
    }

    this.solveButtonEnabled = true;
    this.isLoading = false;
  }

  // @ts-ignore
  onDropZoneEnter(e) {
    if (e.dropZoneElement.id === 'dropzone-external') { this.isDropZoneActive = true; }
  }

  // @ts-ignore
  onDropZoneLeave(e) {
    if (e.dropZoneElement.id === 'dropzone-external') { this.isDropZoneActive = false; }
  }

  // @ts-ignore
  onUploaded(e) {
    const file = e.file;
    const fileReader = new FileReader();
    fileReader.onload = () => {
      this.isDropZoneActive = false;
      this.imageSource = fileReader.result as string;
    };
    fileReader.readAsDataURL(file);
    this.textVisible = false;
    this.progressVisible = false;
    this.progressValue = 0;

    this.solveButtonEnabled = true;
  }

  // @ts-ignore
  onProgress(e) {
    this.progressValue = e.bytesLoaded / e.bytesTotal * 100;
  }

  onUploadStarted() {
    this.imageSource = '';
    this.progressVisible = true;

    this.solveButtonEnabled = false;
  }
}

function delay(ms: number) {
  return new Promise( resolve => setTimeout(resolve, ms) );
}
